<!DOCTYPE html><html><head><meta charset="UTF-8"><style>	form#multiphase{border:#000 1px solid;padding:24px; width:350px;}	form#multiphase>#phase2,#phase3,#show_all_data{display:none;}	#progressBar{width:250px;}</style><script>var fname,lname,gender,country;function _(x){	return document.getElementById(x);}function processPhase1(){	fname=_("firstname").value;	lname=_("lastname").value;	if(fname.length>2 && lname.length>2){	_("phase1").style.display="none";	_("phase2").style.display="block";	_("progressBar").value=33;/*100/3*/	_("status").innerHTML="phase 2 of 3";	}else{ 	alert("Please fill in the fields");	}}function processPhase2(){	gender=_("gender").value;	if(gender.length>0){	_("phase2").style.display="none";	_("phase3").style.display="block";	_("progressBar").value=66;	_("status").innerHTML="phase 3 of 3";	}else{ 	alert("Please choose your gender");	}}function processPhase3(){	country=_("country").value;	if(country.length>0){	_("phase3").style.display="none";	_("show_all_data").style.display="block";	_("display_fname").innerHTML=fname;	_("display_lname").innerHTML=lname;	_("display_gender").innerHTML=gender;	_("display_country").innerHTML=country;	_("progressBar").value=100;	_("status").innerHTML="Data overview";	}else{ 	alert("Please choose your country");	}}function submitForm(){var name=document.getElementById("firstname").value;var lname=document.getElementById("lastname").value;var gender=document.getElementById("gender").value;var country=document.getElementById("country").value;	document.getElementById("result").innerHTML="My Name is " + name + " " +lname + "<br> "+"I am a " +gender + "<br> "+ " I come from " + country;window.location.href = "prog.php?name=" + name+"&lname="+lname+"&gender="+gender+"&country="+country;	}</script></head><body><progress id="progressBar" value="0" max="100" ></progress><h3 id="status">Phase 1 of 3</h3><form id="multiphase" onsubmit="return false" ><div id="phase1">	First Name: <input id="firstname" name="firstname"><br>	Last Name: <input id="lastname" name="lastname"><br>	<button onclick="processPhase1()">Continue </button>	</div>	<div id="phase2">	Gender:<select id="gender" name="gender">		<option value=""></option>		<option value="m">Male</option>		<option value="f">Female</option>		</select><br>	<button onclick="processPhase2()">Continue </button>	</div>	<div id="phase3">	Country:<select id="country" name="country">		<option value="Kenya">Kenya</option>		<option value="Uganda">Uganda</option>		<option value="Tanzania">Tanzania</option>		</select><br>	<button onclick="processPhase3()">Continue </button>	</div>	<div id="show_all_data">	First Name: <span id="display_fname"></span><br>	Last Name: <span id="display_lname"></span><br>	Gender: <span id="display_gender"></span><br>	Country: <span id="display_country"></span><br>	</form>	<input type="submit"   name="submit" onclick="submitForm()"/>	</div>	</form>	<div id="result"></div></body></html>